import styled from 'styled-components';
import {useState,useEffect} from 'react';
import QuestionRow from "../Question/QuestionRow";
import Header1 from "../Header/Header1";
import BlueButtonLink from "../Button/BlueButtonLink";
import axios from "axios";
import {Helmet} from "react-helmet";

const HeaderRow = styled.div`
  display: grid;
  grid-template-columns: 1fr min-content;
  padding: 30px 20px;
`;

function QuestionsPage() {
  const [questions,setQuestions] = useState([]);
  function fetchQuestions() {
    axios.get('http://localhost:3030/questions/questions', {withCredentials:true})
      .then(response => setQuestions(response.data));
  }
  useEffect(() => fetchQuestions(), []);
  return (
    <main>
      <Helmet>
        <title>StackOverflowLite - home</title>
      </Helmet>
      <HeaderRow>
        <Header1 style={{margin:0}}>Questions</Header1>
        <BlueButtonLink to={'/ask'}>Ask&nbsp;Question</BlueButtonLink>
      </HeaderRow>
      {questions && questions.length > 0 && questions.map(question => (
        <QuestionRow
          title={question.title}
          id={question.id}
          createdAt={question.created_at}
          author={{id: question.user_id, name:question.name, email:question.email}}
          tags={question.tags} />
      ))}
    </main>
  );
}

export default QuestionsPage;
